import base64
from io import BytesIO
import qrcode

# The base64 encoded string of the QR code image
qr_code_base64 =""

# Decode the base64 string to get the binary data
qr_code_binary = base64.b64decode(qr_code_base64)

# Save the binary data to an image file
file_path = "./qr_code.png"  # Replace with the desired file path
with open(file_path, "wb") as f:
    f.write(qr_code_binary)

print("QR code image generated and saved successfully!")